Declare 
@ImpressFundAmt decimal(18,2),
@ImpTrans decimal(18,2),
@Totamt decimal(18,2),
@ImpRec decimal(18,2),
@ImpRecTot decimal(18,2),
@impIOU decimal(18,2),
@CashAmt decimal(18,2),
@CurrentValue decimal(18,2)

